// Nice responsive iframe script from Aaron Gustafson: https://gist.github.com/aarongustafson/1313517
function adjustIframes(){$("iframe").each(function(){var e=$(this),t=e.data("proportion"),n=e.attr("width"),r=e.width();if(!t){t=e.attr("height")/n;e.data("proportion",t)}r!=n&&e.css("height",Math.round(r*t)+"px")})}WebFont.load({google:{families:["Open+Sans:400,400italic,700,700italic:latin"]}});var nav=responsiveNav(".nav-collapse",{insert:"after",label:'<i class="icon-menu"></i> Menu'});$(window).on("resize load",adjustIframes);